<?php declare(strict_types=1);

namespace EdgeTelemetrics\EventCorrelation\Tests;

use EdgeTelemetrics\EventCorrelation\Rule;
use EdgeTelemetrics\EventCorrelation\Scheduler;
use EdgeTelemetrics\EventCorrelation\Scheduler\ObservableScheduler;
use PHPUnit\Framework\TestCase;
use React\EventLoop\Loop;
use VStelmakh\PsrTestLogger\TestLogger;
use function sys_get_temp_dir;
use function tempnam;
use function unlink;

require_once __DIR__ . '/../vendor/autoload.php';

/**
 * @covers \EdgeTelemetrics\EventCorrelation\Rule\Cron
 */
class SchedulerRecoveryTests extends TestCase
{
    public function buildObservableScheduler(array $rules = []): ObservableScheduler
    {
        $scheduler = new ObservableScheduler($rules);
        $tempState = tempnam(sys_get_temp_dir(), 'php-ec-test-state-');
        if ($tempState !== false) {
            unlink($tempState);
        }
        $scheduler->setSavefileName($tempState);
        $scheduler->setSaveStateInterval(600);
        return $scheduler;
    }

    public function testRecoveryCompletesAndResumesNormalOperation(): void
    {
        $rule = new class() extends Rule\MatchSingle {
            const EVENTS = [['*']];
        };

        $scheduler = $this->buildObservableScheduler([$rule]);

        $progress = [];
        $scheduler->on('recovery', function() use (&$progress) {
            $progress[] = 'recovery';
        });
        $scheduler->on('running', function() use (&$progress) {
            $progress[] = 'running';
        });
        $scheduler->on('stopping', function() use (&$progress) {
            $progress[] = 'stopping';
        });
        $scheduler->on('stopped', function() use (&$progress) {
            $progress[] = 'stopped';
        });
        $scheduler->on('unclean shutdown', function() use (&$progress) {
            $progress[] = 'unclean shutdown';
        });

        $logger = new TestLogger();
        $scheduler->setLogger($logger);

        $action_run = 0;
        $scheduler->register_action('recovery_job', function () use (&$action_run, $scheduler) {
            $def = new \React\Promise\Deferred();
            Loop::addTimer(2, static function () use ($def, &$action_run, $scheduler) {
                $action_run++;
                $def->resolve("complete");
                Loop::futureTick($scheduler->shutdown(...));
            });
            return $def->promise();
        });

        $event = null;
        $scheduler->setHandleEventCallback(static function ($received) use ($scheduler, &$event) {
            $event ??= $received;
            //if (!$scheduler->getExecutionState()->isStopping()) {
            //    $scheduler->shutdown();
            //}
        }, null);

